<?phpif(XT::getParam('source_type') != ""){    $source_type = 'content_type=' . XT::getParam('source_type');}else {    $stop = true;}if(XT::getParam('source_id') != ""){    $source_id = ' AND content_id=' . XT::getParam('source_id');}else {    $in = "";    if(is_array($GLOBALS['relations'][XT::getParam('source_type')])){        foreach ($GLOBALS['relations'][XT::getParam('source_type')] as $key => $value) {            $in .= "," . $key;        }        if($in!=''){            $source_id = ' AND content_id IN(' . substr($in,1) . ")";        }    }else{        $stop = true;    }}if(!$stop){    $result = XT::query("SELECT rel.* FROM " . XT::getTable('relations') . " as rel    WHERE "    . $source_type    . $source_id    . "  ORDER by position "    ,__FILE__,__LINE__);    $data['DATA'] = XT::getQueryData($result);    foreach ($data['DATA'] as $key => $value) {        $data['GROUPEDDATA'][$value['content_type']][] = $value;    }    XT::assign("xt" . XT::getBaseID() . "_fulllist_reverse", $data);    // Fetch content    if($GLOBALS['plugin']->getParam("style") != ""){        $style = $GLOBALS['plugin']->getParam("style");    }else{        $style = "default.tpl";    }    $content = XT::build($style);}?>